import React, { useEffect, useState } from 'react';
import axios from 'axios';


const Home = () => {
    const [animal, setAnimal] = useState(null); // Now a single object

    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchAnimalData = async () => {
            try {
                const response = await axios.get('http://localhost:5000/animals');
                setAnimal(response.data); // Set the single animal object
            } catch (error) {
                console.error('Error fetching animal data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchAnimalData();
    }, []);

    if (loading) return <div>Loading...</div>;

    return (
        <div className="Home">
            {animal ? ( // Check if animal is not null
                <div className="animal-details">
                    <h1>{animal.name}</h1>
                    <img src={animal.image_url} alt={animal.name} style={{ width: '300px', height: 'auto' }} />
                    <p>{animal.description}</p>
                </div>
            ) : (
                <p>No animal data available.</p>
            )}
        </div>
    );
};

export default Home;
