import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        firstname: '',
        lastname: '',
        username: '',
        email: '',
        password: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Retrieve the username from local storage
        const storedUsername = localStorage.getItem('username');
        if (storedUsername) {
            fetchProfile(storedUsername);
        } else {
            setMessage("No username found. Please log in.");
        }
    }, []);

    const fetchProfile = async (username) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/profile/${username}`);
            setProfile(response.data);
        } catch (error) {
            console.error("Error fetching profile:", error);
            setMessage("Failed to load profile.");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile((prevProfile) => ({ ...prevProfile, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const storedUsername = localStorage.getItem('username');
            const response = await axios.put(`http://localhost:8080/api/profile/${storedUsername}`, profile);
            setProfile(response.data);
            setIsEditing(false);
            setMessage("Profile updated successfully.");
        } catch (error) {
            console.error("Error updating profile:", error);
            setMessage("Failed to update profile.");
        }
    };

    return (
        <div>
            <h2>User Profile</h2>
            {message && <p>{message}</p>}
            {isEditing ? (
                <form onSubmit={handleSubmit}>
                    <input type="text" name="firstname" value={profile.firstname} onChange={handleChange} />
                    <input type="text" name="lastname" value={profile.lastname} onChange={handleChange} />
                    <input type="text" name="username" value={profile.username} onChange={handleChange} />
                    <input type="email" name="email" value={profile.email} onChange={handleChange} />
                    <input type="password" name="password" value={profile.password} onChange={handleChange} />
                    <button type="submit">Save</button>
                    <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
                </form>
            ) : (
                <div>
                    <p>First Name: {profile.firstname}</p>
                    <p>Last Name: {profile.lastname}</p>
                    <p>Username: {profile.username}</p>
                    <p>Email: {profile.email}</p>
                    <button onClick={() => setIsEditing(true)}>Edit Profile</button>
                </div>
            )}
        </div>
    );
};

export default ProfilePage;
