import React, { useState } from 'react';
import axios from 'axios';
import './login.css'; // Import the CSS

const LoginPage = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        userType: 'user' // Default user type
    });

    const [error, setError] = useState(''); // For error messages
    const [message, setMessage] = useState(''); // For success messages

    // Handle input changes
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Clear previous messages
            setError('');
            setMessage('');

            // Make a POST request to the backend login endpoint
            const response = await axios.post('http://localhost:8080/api/login', formData);

            // Check if the response contains a redirect path
            if (response.data.status === 'success' && response.data.redirect) {
                // Redirect to the specified path
                window.location.href = response.data.redirect;
            } else {
                // Display success message if there's no redirect
                setMessage('Login successful');
            }
        } catch (error) {
            // Handle error
            if (error.response && error.response.data) {
                setError(error.response.data.message || 'Login failed. Please try again.'); // Display backend error message if available
            } else {
                setError('Login failed. Please try again.');
            }
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <input
                        type="text"
                        name="username"
                        placeholder="Username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <input
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <select name="userType" value={formData.userType} onChange={handleChange}>
                        <option value="user">user</option>
                        <option value="Admin">Admin</option>
                    </select>
                </div>
                <button type="submit">Login</button>

                {/* Display success or error message below the button */}
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
};

export default LoginPage;
