// components/MapPage.jsx
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './MapPage.css';
// Example icon for animals
const animalIcon = new L.Icon({
    iconUrl: '../image/download (1).jpeg', // Use an icon URL or a specific icon for animals
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
});

// Sample data for animal locations
const animalLocations = [
    { id: 1, name: "Lion", location: [51.505, -0.09], description: "Lion spotted here!" },
    { id: 2, name: "Elephant", location: [51.515, -0.1], description: "Elephant habitat" },
    { id: 3, name: "Zebra", location: [51.525, -0.11], description: "Zebra roaming area" },
];

const MapPage = () => (
    <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: "80vh", width: "100%" }}>
        <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        {animalLocations.map((animal) => (
            <Marker key={animal.id} position={animal.location} icon={animalIcon}>
                <Popup>
                    <strong>{animal.name}</strong>
                    <p>{animal.description}</p>
                </Popup>
            </Marker>
        ))}
    </MapContainer>
);

export default MapPage;
