import React, { useState, useEffect } from 'react';
import axios from 'axios';
const ProfilePage = ({ username }) => {
    const [profile, setProfile] = useState({
        firstname: '',
        lastname: '',
        username: '', // Add username field
        email: '',
        password: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/api/profile/${username}`);
                setProfile(response.data);
            } catch (error) {
                console.error("Error fetching profile:", error);
            }
        };
        fetchProfile();
    }, [username]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile((prevProfile) => ({ ...prevProfile, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put(`http://localhost:8080/api/profile/${username}`, profile);
            setProfile(response.data);
            setIsEditing(false);
            setMessage("Profile updated successfully");
        } catch (error) {
            console.error("Error updating profile:", error);
            setMessage("Failed to update profile");
        }
    };

    return (
        <div>
            <h2>User Profile</h2>
            {message && <p>{message}</p>}
            {isEditing ? (
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>First Name:</label>
                        <input type="text" name="firstname" value={profile.firstname} onChange={handleChange} />
                    </div>
                    <div>
                        <label>Last Name:</label>
                        <input type="text" name="lastname" value={profile.lastname} onChange={handleChange} />
                    </div>
                    <div>
                        <label>Username:</label> {/* Add username field */}
                        <input type="text" name="username" value={profile.username} onChange={handleChange} />
                    </div>
                    <div>
                        <label>Email:</label>
                        <input type="email" name="email" value={profile.email} onChange={handleChange} />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input type="password" name="password" value={profile.password} onChange={handleChange} />
                    </div>
                    <button type="submit">Save</button>
                    <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
                </form>
            ) : (
                <div>
                    <p>First Name: {profile.firstname}</p>
                    <p>Last Name: {profile.lastname}</p>
                    <p>Username: {profile.username}</p> {/* Display username */}
                    <p>Email: {profile.email}</p>
                    <button onClick={() => setIsEditing(true)}>Edit Profile</button>
                </div>
            )}
        </div>
    );
};

export default ProfilePage;
