import React, { useEffect, useState } from 'react';
import axios from 'axios';


const Home = () => {
    const [animal, setAnimal] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchAnimalData = async () => {
            try {
                const response = await axios.get('http://localhost:5000/animals/current'); // Updated endpoint
                setAnimal(response.data); // Assuming it returns all animals
            } catch (error) {
                console.error('Error fetching animal data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchAnimalData();
    }, []);

    if (loading) return <div>Loading...</div>;

    return (
        <div className="Home">
            {animal.length > 0 ? ( // Check if there's any animal data
                animal.map((a) => ( // Map through all animals if necessary
                    <div className="animal-details" key={a.id}>
                        <h1>{a.name}</h1>
                        <img src={a.image_url} alt={a.name} style={{ width: '300px', height: 'auto' }} />
                        <p>{a.description}</p>
                    </div>
                ))
            ) : (
                <p>No animal data available.</p>
            )}
        </div>
    );
};

export default Home;
