import React, { useEffect } from 'react';
import maplibregl from 'maplibre-gl';
import './MapPage.css';

const MapPage = () => {
    useEffect(() => {
        const map = new maplibregl.Map({
            container: 'map',
            style: 'https://demotiles.maplibre.org/style.json',
            center: [78.9629, 20.5937],
            zoom: 5,
        });

        const animalLocations = [
            {
                name: "Bengal Tiger",
                coordinates: [79.4082, 21.2945],
                imageUrl: "https://example.com/bengal_tiger.jpg",
            },
            {
                name: "Asian Elephant",
                coordinates: [77.8809, 11.5860],
                imageUrl: "https://example.com/asian_elephant.jpg",
            },
            {
                name: "Indian Rhino",
                coordinates: [93.3656, 26.0950],
                imageUrl: "https://example.com/indian_rhino.jpg",
            },
            {
                name: "Snow Leopard",
                coordinates: [78.7320, 32.6512],
                imageUrl: "https://example.com/snow_leopard.jpg",
            },
            {
                name: "Great Indian Bustard",
                coordinates: [70.7211, 26.0960],
                imageUrl: "https://example.com/great_indian_bustard.jpg",
            },
        ];

        // Log coordinates to debug
        animalLocations.forEach(animal => {
            console.log(`Adding marker for ${animal.name} at coordinates: ${animal.coordinates}`);
            const marker = new maplibregl.Marker()
                .setLngLat(animal.coordinates)
                .setPopup(new maplibregl.Popup().setHTML(`
                    <strong>${animal.name}</strong><br>
                    <img src="${animal.imageUrl}" alt="${animal.name}" style="width:100px; height:auto;">
                `))
                .addTo(map);
        });

        map.on('load', () => {
            console.log('Map loaded successfully');
        });

        map.on('error', (e) => {
            console.error('Map error:', e);
        });

        return () => map.remove();
    }, []);

    return (
        <div id="map" style={{ height: '100vh', width: '100vw' }} />
    );
};

export default MapPage;
