import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = () => {
    const [userDetails, setUserDetails] = useState(null);
    const [error, setError] = useState('');

    const fetchUserProfile = async () => {
        const username = localStorage.getItem('username'); // Get the username from local storage
        try {
            const response = await axios.get(`http://localhost:8080/api/profile/${username}`);
            if (response.status === 200) {
                setUserDetails(response.data); // Set user details
            }
        } catch (error) {
            setError('Failed to load profile.'); // Handle errors
        }
    };

    // Call fetchUserProfile when the component mounts
    React.useEffect(() => {
        fetchUserProfile();
    }, []);

    return (
        <div>
            <h2>User Profile</h2>
            {message && <p>{message}</p>}
            {profile ? (
                <div>
                    <p>First Name: {profile.firstname}</p>
                    <p>Last Name: {profile.lastname}</p>
                    <p>Username: {profile.username}</p>
                    <p>Email: {profile.email}</p>
                </div>
            ) : (
                <p>Loading profile...</p>
            )}
        </div>
    );
};

export default ProfilePage;
