import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './home.css';

const Home = () => {
    const [animal, setAnimal] = useState(null);

    useEffect(() => {
        const fetchAnimalData = async () => {
            try {
                const response = await axios.get('http://localhost:5000/animals/current');
                setAnimal(response.data);
            } catch (error) {
                console.error('Error fetching animal data:', error);
            }
        };

        fetchAnimalData();
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.scrollY;
            const homeElement = document.querySelector('.home');
            if (scrollTop > 50) {
                homeElement.classList.add('scrolled');
            } else {
                homeElement.classList.remove('scrolled');
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className="home">
            {animal && (
                <div className="animal-section">
                    <img
                        src={`http://localhost:5000/images/${animal.image_url}`}
                        alt={animal.name}
                        className="animal-image"
                    />
                    <div className="animal-details">
                        <h1>{animal.name}</h1>
                        <p>{animal.description}</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
