import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './profile.css'; // You can add your CSS file for styling


const ProfilePage = () => {
    const [userDetails, setUserDetails] = useState(null);
    const [error, setError] = useState('');

    const fetchUserProfile = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/user-details');
            if (response.status === 200) {
                setUserDetails(response.data); // Set user details
            }
        } catch (error) {
            setError('Failed to load profile.'); // Handle errors
            console.error('Error fetching user profile:', error); // Log the error for debugging
        }
    };

    // Call fetchUserProfile when the component mounts
    useEffect(() => {
        fetchUserProfile();
    }, []);

    return (
        <div className="profile-container">
            <h2>User Profile</h2>
            {error && <p className="error-message">{error}</p>}
            {userDetails ? (
                <div>
                    <p><strong>First Name:</strong> {userDetails.firstname}</p>
                    <p><strong>Last Name:</strong> {userDetails.lastname}</p>
                    <p><strong>Username:</strong> {userDetails.username}</p>
                    <p><strong>Email:</strong> {userDetails.email}</p>
                    <p><strong>Password:</strong> {userDetails.password}</p>
                    {/* Add other user details as needed */}
                </div>
            ) : (
                <p>Loading user details...</p>
            )}
        </div>
    );
};

export default ProfilePage;
