import React, { useEffect } from 'react';

const animalLocations = [
    {
        name: "Bengal Tiger",
        coordinates: { lat: 21.2945, lng: 79.4082 }, // Tadoba Andhari Tiger Reserve
        imageUrl: "https://example.com/bengal_tiger.jpg", // Replace with actual image URL
    },
    {
        name: "Asian Elephant",
        coordinates: { lat: 11.5860, lng: 77.8809 }, // Mudumalai National Park
        imageUrl: "https://example.com/asian_elephant.jpg", // Replace with actual image URL
    },
    {
        name: "Indian Rhino",
        coordinates: { lat: 26.0950, lng: 93.3656 }, // Kaziranga National Park
        imageUrl: "https://example.com/indian_rhino.jpg", // Replace with actual image URL
    },
    {
        name: "Snow Leopard",
        coordinates: { lat: 32.6512, lng: 78.7320 }, // Hemis National Park
        imageUrl: "https://example.com/snow_leopard.jpg", // Replace with actual image URL
    },
    {
        name: "Great Indian Bustard",
        coordinates: { lat: 26.0960, lng: 70.7211 }, // Desert National Park
        imageUrl: "https://example.com/great_indian_bustard.jpg", // Replace with actual image URL
    },
];

const MapPage = () => {
    useEffect(() => {
        const map = new window.google.maps.Map(document.getElementById('map'), {
            center: { lat: 20.5937, lng: 78.9629 }, // Center of the map (India)
            zoom: 5, // Initial zoom level
        });

        // Add markers for each animal location
        animalLocations.forEach(animal => {
            const marker = new window.google.maps.Marker({
                position: animal.coordinates,
                map: map,
                title: animal.name,
            });

            const infowindow = new window.google.maps.InfoWindow({
                content: `
                    <div style="text-align: center;">
                        <strong>${animal.name}</strong><br>
                        <img src="${animal.imageUrl}" alt="${animal.name}" style="width: 100px; height: auto;">
                    </div>
                `,
            });

            // Show info window when marker is clicked
            marker.addListener('click', () => {
                infowindow.open(map, marker);
            });
        });
    }, []);

    return (
        <div id="map" style={{ height: '100vh', width: '100vw' }} />
    );
};

// Load the Google Maps script dynamically
const loadGoogleMapsScript = () => {
    return new Promise((resolve) => {
        const existingScript = document.getElementById('google-maps');

        if (!existingScript) {
            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=`; // Replace with your API key
            script.id = 'google-maps';
            script.onload = () => {
                resolve(true);
            };
            document.body.appendChild(script);
        } else {
            resolve(true);
        }
    });
};

const App = () => {
    useEffect(() => {
        loadGoogleMapsScript();
    }, []);
