import React, { useEffect } from 'react';
import maplibregl from 'maplibre-gl';
import './MapPage.css';

// Sample data for animal locations with images
const animalLocations = [
    {
        name: "Bengal Tiger",
        coordinates: [79.4082, 21.2945], // Tadoba Andhari Tiger Reserve
        imageUrl: "https://example.com/bengal_tiger.jpg", // Replace with actual image URL
    },
    {
        name: "Asian Elephant",
        coordinates: [77.8809, 11.5860], // Mudumalai National Park
        imageUrl: "https://example.com/asian_elephant.jpg", // Replace with actual image URL
    },
    {
        name: "Indian Rhino",
        coordinates: [93.3656, 26.0950], // Kaziranga National Park
        imageUrl: "https://example.com/indian_rhino.jpg", // Replace with actual image URL
    },
    {
        name: "Snow Leopard",
        coordinates: [78.7320, 32.6512], // Hemis National Park
        imageUrl: "https://example.com/snow_leopard.jpg", // Replace with actual image URL
    },
    {
        name: "Great Indian Bustard",
        coordinates: [70.7211, 26.0960], // Desert National Park
        imageUrl: "https://example.com/great_indian_bustard.jpg", // Replace with actual image URL
    },
];

const MapPage = () => {
    useEffect(() => {
        // Initialize the map
        const map = new maplibregl.Map({
            container: 'map', // ID of the container
            style: 'https://demotiles.maplibre.org/style.json', // Map style
            center: [78.9629, 20.5937], // Center of the map (India)
            zoom: 5, // Initial zoom level
        });

        // Add markers for each animal location
        animalLocations.forEach(animal => {
            const marker = new maplibregl.Marker()
                .setLngLat(animal.coordinates)
                .setPopup(new maplibregl.Popup().setHTML(`
                    <div style="text-align: center;">
                        <strong>${animal.name}</strong><br>
                        <img src="${animal.imageUrl}" alt="${animal.name}" style="width: 100px; height: auto;">
                    </div>
                `)) // Popup with animal name and image
                .addTo(map);
        });

        // Optional: Fit the map to the markers
        const bounds = new maplibregl.LngLatBounds();
        animalLocations.forEach(animal => {
            bounds.extend(animal.coordinates);
        });
        map.fitBounds(bounds, { padding: 20 });

        return () => map.remove(); // Cleanup on component unmount
    }, []);

    return (
        <div id="map" style={{ height: '100vh', width: '100vw' }} />
    );
};

export default MapPage;
