// Encyclopedia.js
import React, { useState } from 'react';
import axios from 'axios';
import AlphabetFilter from './AlphabetFilter';

const Encyclopedia = () => {
    const [animals, setAnimals] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleLetterClick = async (letter) => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`http://localhost:5000/animal?letter=${letter}`);
            setAnimals(response.data);
        } catch (err) {
            setError('Error fetching animal data.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Animal Encyclopedia</h1>
            <AlphabetFilter onLetterClick={handleLetterClick} />
            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}
            {animals.length > 0 ? (
                <ul>
                    {animals.map(animal => (
                        <li key={animal.id}>{animal.name}</li>
                    ))}
                </ul>
            ) : (
                <p>No animals found for the selected letter.</p>
            )}
        </div>
    );
};

export default Encyclopedia;
