// MapPage.jsx
import React, { useEffect } from 'react';
import maplibregl from 'maplibre-gl';
import './MapPage.css';

const MapPage = () => {
    useEffect(() => {
        const map = new maplibregl.Map({
            container: 'map', // ID of the container
            style: 'https://demotiles.maplibre.org/style.json', // URL of the style
            center: [78.9629, 20.5937], // Center of the map (India)
            zoom: 5, // Initial zoom level
        });

        // Sample animal locations (longitude, latitude)
        const animalLocations = [
            {
                name: "Bengal Tiger",
                coordinates: [79.4082, 21.2945], // Example: Tadoba Andhari Tiger Reserve
            },
            {
                name: "Asian Elephant",
                coordinates: [77.8809, 11.5860], // Example: Mudumalai National Park
            },
            {
                name: "Indian Rhino",
                coordinates: [93.3656, 26.0950], // Example: Kaziranga National Park
            },
            {
                name: "Snow Leopard",
                coordinates: [78.7320, 32.6512], // Example: Hemis National Park
            },
            {
                name: "Great Indian Bustard",
                coordinates: [70.7211, 26.0960], // Example: Desert National Park
            },
        ];

        // Add markers for each animal location
        animalLocations.forEach(animal => {
            const marker = new maplibregl.Marker()
                .setLngLat(animal.coordinates)
                .setPopup(new maplibregl.Popup().setHTML(`<strong>${animal.name}</strong>`)) // Popup with animal name
                .addTo(map);
        });

        return () => map.remove(); // Cleanup on component unmount
    }, []);

    return (
        <div id="map" style={{ height: '200vh', width: '200%' }} />
    );
};

export default MapPage;
