import React, { useState } from 'react';
import axios from 'axios';
import './login.css';

const LoginPage = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        userType: 'user'
    });
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setError(''); // Reset error message
            setMessage(''); // Reset success message

            const response = await axios.post('http://localhost:8080/api/login', formData);

            // On successful login, store username and userType in local storage
            if (response.data.status === 'success') {
                localStorage.setItem('username', formData.username);
                localStorage.setItem('userType', formData.userType); // Store userType
                setMessage("Login successful!");

                // Redirect based on userType
                window.location.href = formData.userType === 'admin' ? '/dashboard' : '/home';
            } else {
                setError("Login failed. Please check your credentials.");
            }
        } catch (error) {
            console.error('Login error:', error); // Log the error for debugging
            if (error.response && error.response.data) {
                // Adjust the error message based on server response
                setError(error.response.data.message || 'Login failed. Please try again.');
            } else {
                setError('Login failed. Please try again.');
            }
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
                <select name="userType" value={formData.userType} onChange={handleChange}>
                    <option value="user">User</option>
                    <option value="admin">Admin</option>
                </select>
                <button type="submit">Login</button>

                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
};

export default LoginPage;
