import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './profile.css';

const ProfilePage = () => {
    const [userDetails, setUserDetails] = useState(null);
    const [error, setError] = useState('');

    // Fetch user profile based on the username stored in the session
    const fetchUserProfile = async () => {
        const username = localStorage.getItem('username'); // Get username from local storage
        if (!username) {
            setError('No user logged in.');
            return;
        }
        try {
            const response = await axios.get(`http://localhost:3000/api/user-details/${username}`);
            if (response.status === 200) {
                setUserDetails(response.data); // Set user details
            }
        } catch (error) {
            setError('Failed to load profile.'); // Handle errors
            console.error('Error fetching user profile:', error);
        }
    };

    useEffect(() => {
        fetchUserProfile();
    }, []);

    return (
        <div className="profile-container">
            <h2>User Profile</h2>
            {error && <p className="error-message">{error}</p>}
            {userDetails ? (
                <div>
                    <p><strong>Username:</strong> {userDetails.username}</p>
                    <p><strong>Email:</strong> {userDetails.email}</p>
                    <p><strong>First Name:</strong> {userDetails.firstname}</p>
                    <p><strong>Last Name:</strong> {userDetails.lastname}</p>
                    <p><strong>Password:</strong> {userDetails.password}</p> {/* Avoid showing passwords */}
                </div>
            ) : (
                <p>Loading user details...</p>
            )}
        </div>
    );
};

export default ProfilePage;
