// components/MapPage.jsx
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Example icon for animals (ensure you have a valid path or URL for the icon)
const animalIcon = new L.Icon({
    iconUrl: 'path/to/animal-icon.png', // Replace with the actual path to your icon
    iconSize: [25, 41], // Size of the icon
    iconAnchor: [12, 41], // Point of the icon which will correspond to marker's location
    popupAnchor: [1, -34], // Point from which the popup should open relative to the iconAnchor
});

// Sample data for animal locations in India
const animalLocations = [
    { id: 1, name: "Bengal Tiger", location: [21.2945, 79.4082], description: "Tadoba Andhari Tiger Reserve, Maharashtra" },
    { id: 2, name: "Asian Elephant", location: [11.5860, 77.8809], description: "Mudumalai National Park, Tamil Nadu" },
    { id: 3, name: "Indian Rhino", location: [26.0950, 93.3656], description: "Kaziranga National Park, Assam" },
    { id: 4, name: "Snow Leopard", location: [32.6512, 78.7320], description: "Hemis National Park, Ladakh" },
    { id: 5, name: "Great Indian Bustard", location: [26.0960, 70.7211], description: "Desert National Park, Rajasthan" },
];

const MapPage = () => (
    <MapContainer center={[20.5937, 78.9629]} zoom={5} style={{ height: "80vh", width: "100%" }}>
        <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        {animalLocations.map((animal) => (
            <Marker key={animal.id} position={animal.location} icon={animalIcon}>
                <Popup>
                    <strong>{animal.name}</strong>
                    <p>{animal.description}</p>
                </Popup>
            </Marker>
        ))}
    </MapContainer>
);

export default MapPage;
